import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [theme, setTheme] = useState('default');
  const [option, setOption] = useState('generate');

  const handleImageChange = (e) => {
    setSelectedImage(e.target.files[0]);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', selectedImage);
    formData.append('prompt', prompt);
    formData.append('theme', theme);
    formData.append('option', option);

    try {
      const response = await axios.post('http://localhost:8000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log(response.data);
    } catch (error) {
      console.error('Error uploading the image:', error);
    }
  };

  return (
    <div className="app">
      <h1>Image Generator & Upscaler</h1>
      <form onSubmit={handleFormSubmit}>
        <label>
          Upload Image:
          <input type="file" onChange={handleImageChange} />
        </label>
        <label>
          Prompt:
          <input type="text" value={prompt} onChange={(e) => setPrompt(e.target.value)} />
        </label>
        <label>
          Theme:
          <select value={theme} onChange={(e) => setTheme(e.target.value)}>
            <option value="default">Default</option>
            <option value="historic">Historic</option>
            <option value="funky">Funky</option>
            <option value="abstract">Abstract</option>
            <option value="pixelart">Pixel Art</option>
            <option value="modern">Modern</option>
            {/* Add more themes as needed */}
          </select>
        </label>
        <label>
          Options:
          <select value={option} onChange={(e) => setOption(e.target.value)}>
            <option value="generate">Generate Image</option>
            <option value="upscale">Upscale Image</option>
            <option value="variations">Create Variations</option>
          </select>
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
